import React, { useState } from "react";

export const SmapleContext = React.createContext<{
  val: string;
  setVal: (v: string) => any;
}>({
  val: "",
  setVal: (v: string) => {},
});

export const SmapleProvider: React.FC<{}> = ({ children }) => {
  const [_val, set_val] = useState("");

  const smapleContext = React.useMemo(
    () => ({
      val: _val,
      setVal: (v: string) => {
        if (_val == v) return;
        set_val(v);
        console.log("hello");
      },
    }),
    [_val]
  );

  return (
    <SmapleContext.Provider value={smapleContext}>
      {children}
    </SmapleContext.Provider>
  );
};
