import Link from "@app/components/Link";
import React, { useContext } from "react";
import useAppLocale from "@app/hooks/useAppLocale";
import styles from "./About.module.scss";
import { BrowserView, MobileView } from "react-device-detect";
import { Button, Container } from "react-bootstrap";
import useAppTheme from "@app/hooks/useAppTheme";
import { ThemeContext } from "@app/Providers/ThemeProvider";
import { SmapleContext } from "@app/Providers/SmplaeProvider";

const About = () => {
  const { t } = useAppLocale();
  const { val, setVal } = useContext(SmapleContext);

  return (
    <Container>
      <h2 className={styles.hello}>{t("about.title")}</h2>
      <Link to="/">Back</Link>
      <BrowserView>
        <h1> This is rendered only in browser </h1>
      </BrowserView>
      <MobileView>
        <h1> This is rendered only on mobile </h1>
      </MobileView>

      <Button
        variant="primary"
        onClick={() => {
          setVal("ok");
        }}
      >{`switch to ${val}`}</Button>
    </Container>
  );
};

export default About;
