import React, { useState } from "react";

export const SampleContext = React.createContext<{
  currentSample: string;
  setSample: (newSample: string) => any;
}>({
  currentSample: "",
  setSample: (newSample: string) => {},
});

export const SampleProvider: React.FC<{}> = ({ children }) => {
  const [_val, set_val] = useState("");

  const _context = React.useMemo(
    () => ({
      currentSample: _val,
      setSample: (newSample: string) => {
        if (_val == newSample) return;
        set_val(newSample);
      },
    }),
    [_val]
  );

  return (
    <SampleContext.Provider value={_context}>{children}</SampleContext.Provider>
  );
};
